$('.subscribe').ajaxChimp({
	callback: mailchimpCallback,
	url: "http://kenulja.us10.list-manage.com/subscribe/post?u=c44c6be78037e6b98cf1d1b6a&amp;id=d28f0fcdc1" //Replace this with your own mailchimp post URL. Don't remove the "". Just paste the url inside "".  
});

function mailchimpCallback(resp) {
	if (resp.result === 'success') {
		$('h6.success').html('<i class="pe-7s-check"></i>' + resp.msg).fadeIn(1000);
		$('h6.error').fadeOut(500);
	}
	else if (resp.result === 'error') {
		$('h6.error').html('<i class="pe-7s-close-circle"></i>' + resp.msg).fadeIn(1000);
	}
}